from pint import UnitRegistry

u = UnitRegistry()

def convert(value: float, from_unit:str, to_unit:str):
    '''Convert value between units using Pint.'''
    try:
        quantity = value * u(from_unit)
        return quantity.to(to_unit).magnitude
    except Exception:
        return None


